import React from 'react';
import './App.css';
import './components/codeIn/CodeIn'
import CodeIn from './components/codeIn/CodeIn';
import DisplayArea from './components/DisplayArea/DisplayArea'


class App extends React.Component {
  constructor(){

    super();
    this.state = {
      name:'aditya',
      input:'',
      messageList:[{name:"aditya",messageSent:"Hello World"}],
      message:'',
      route:'signin'
    }
  }

  onCodeChange = (event) =>{
    console.log(this.state.input);
    this.setState({input:event.target.value});

  }


  onMessageChange = (event) =>{
    console.log(this.state.input);
    this.setState({message:event.target.value});

  }

  onSubmit = () =>{

    if(this.state.input){
      this.setState({
        route:"signedIn",
        name:this.state.input
      })
    }
  }

  onSend = () =>{
    if(this.state.message){
      this.setState({
        messageList:this.state.messageList+[{name:this.state.name,messageSent:this.state.message}]
      });
    }
  }



  render(){
    if(this.state.route==="signin"){
      return(
        <div className = "App">
        <CodeIn onSubmit={this.onSubmit} onCodeChange = {this.onCodeChange} route ={this.state.route}/>
      </div>
      )
    }
    else{
      return(
      <DisplayArea  onMessageChange={this.onMessageChange}  messageList={this.state.messageList}/>
      )
    }
  }
}

export default App;
