import React from 'react';
import './App.css';
import './components/codeIn/CodeIn'
import CodeIn from './components/codeIn/CodeIn';
import DisplayArea from './components/DisplayArea/DisplayArea'
import io from "socket.io-client";

const socket = io.connect('/');






class App extends React.Component {
  constructor(){

    super();
    this.state = {
      name:'',
      input:'',
      messageList:[],
      message:'',
      route:'signin'
    }
  }

  componentDidMount(){
    console.log("mounted");

    socket.on('chatMessage', data =>{
      console.log(data)
      this.setState({messageList : this.state.messageList.concat(data)})
      console.log(this.state.messageList);
    })

    fetch('http://localhost:3000/test')
    .then(resp => resp.json())
    .then(data => console.log(data));

  }


  onNameChange = (event) =>{
    console.log(this.state.input);
    this.setState({input:event.target.value});

  }


  onMessageChange = (event) =>{
    this.setState({message:event.target.value});

  }

  onSubmit = () =>{

    if(this.state.input){
      this.setState({
        route:"signedIn",
        name:this.state.input
      })
    }
  }

  onSend = () =>{
    if(this.state.message){
      this.setState({
        messageList:this.state.messageList.concat([{name:this.state.name,messageSent:this.state.message}])
      });
    }

    fetch()
  }



  render(){
    if(this.state.route==="signin"){
      return(
        <div className = "App">
        <CodeIn onSubmit={this.onSubmit} onNameChange = {this.onNameChange}/>
      </div>
      )
    }
    else{
      return(
        <div className="App">
          <DisplayArea  onMessageChange={this.onMessageChange}  messageList={this.state.messageList} onSend={this.onSend}/>
        </div>
      )
    }
  }
}

export default App;
