import React from 'react';
import './App.css';
import './components/codeIn/CodeIn'
import CodeIn from './components/codeIn/CodeIn';
import DisplayArea from './components/DisplayArea/DisplayArea'


class App extends React.Component {
  constructor(){

    super();
    this.state = {
      input:'',
      items:["1","2","3","4"],
      last:''
    }
  }

  onInputChange = (event) =>{
    console.log(this.state.input);
    this.setState({input:event.target.value});

  }

  onSubmit = () =>{
    // let temp =this.state.items;
    // if(this.state.input!==""){
    //   temp.push(this.state.input);
    // }
    // this.setState({
    //   items:temp
    // })


    if(this.state.input){
      this.setState({
        last:this.state.input
      })
      console.log("last:",this.state.last);
    }


  }



  render(){

    return(
      <div className = "App">
        <CodeIn onSubmit={this.onSubmit} onInputChange = {this.onInputChange}/>
        <hr></hr>
        <DisplayArea/>
      </div>
    );
  }
}

export default App;
